package com.example.batterymonitor

import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData

object EspIpRepository {
    private val _espIp = MutableLiveData<String>("192.168.1.99")
    val espIp: LiveData<String> get() = _espIp

    fun setIp(ip: String) {
        _espIp.value = ip
    }
}
